import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Button, Progress, Tabs, Table, Statistic, Alert, Divider, Tag, Descriptions, Steps } from 'antd';
import { CheckCircleOutlined, CloseCircleOutlined, InfoCircleOutlined, RocketOutlined, DatabaseOutlined, BarChartOutlined } from '@ant-design/icons';
import { ResponsiveContainer, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, LineChart, Line, PieChart, Pie, Cell, ScatterChart, Scatter, AreaChart, Area } from 'recharts';

const { TabPane } = Tabs;
const { Step } = Steps;

function App() {
  const [selectedMethods, setSelectedMethods] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [currentStep, setCurrentStep] = useState(0);
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);

  // Cargar datos reales del servidor
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await fetch('/api/dashboard-data');
        const data = await response.json();
        setDashboardData(data);
        setLoading(false);
      } catch (error) {
        console.error('Error cargando datos:', error);
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  // Usar datos reales del servidor o datos por defecto
  const metodosML = dashboardData?.resultados || [];

  const chartData = [
    { name: 'Árbol de Decisión', accuracy: 100.00, auc: 100.00 },
    { name: 'HistGradientBoosting', accuracy: 100.00, auc: 100.00 },
    { name: 'Regresión Logística', accuracy: 98.73, auc: 95.29 },
    { name: 'Random Forest', accuracy: 98.73, auc: 99.78 },
    { name: 'K-Nearest Neighbors', accuracy: 98.41, auc: 88.73 },
    { name: 'Support Vector Machine', accuracy: 98.41, auc: 85.14 },
    { name: 'Naive Bayes', accuracy: 87.30, auc: 81.59 },
    { name: 'K-Means Clustering', accuracy: 51.27, auc: 51.27 },
    { name: 'ARIMA', accuracy: 82.00, auc: 82.00 },
    { name: 'Suavizado Exponencial', accuracy: 85.00, auc: 85.00 }
  ];

  const processSteps = [
    { title: "Carga de Datos", description: "1,571 artículos periodísticos cargados", progress: 20 },
    { title: "Limpieza de Texto", description: "Procesamiento de títulos, resúmenes y contenido", progress: 40 },
    { title: "Vectorización TF-IDF", description: "1,000 características de texto generadas", progress: 60 },
    { title: "Entrenamiento ML", description: "10 algoritmos entrenados", progress: 80 },
    { title: "Evaluación", description: "Métricas calculadas y comparación", progress: 100 }
  ];

  const toggleMethod = (methodId) => {
    if (selectedMethods.includes(methodId)) {
      setSelectedMethods(selectedMethods.filter(id => id !== methodId));
    } else {
      setSelectedMethods([...selectedMethods, methodId]);
    }
  };

  const executeAnalysis = () => {
    if (selectedMethods.length === 0) {
      alert('Por favor selecciona al menos un método para analizar.');
      return;
    }

    setIsAnalyzing(true);
    setCurrentStep(0);

    const interval = setInterval(() => {
      setCurrentStep(prev => {
        if (prev >= processSteps.length - 1) {
          clearInterval(interval);
          setIsAnalyzing(false);
          setAnalysisResults({
            selectedMethods: selectedMethods,
            results: metodosML.filter(m => selectedMethods.includes(m.id)),
            bestMethod: metodosML.find(m => m.id === selectedMethods[0])
          });
          return prev;
        }
        return prev + 1;
      });
    }, 1000);
  };

  const getMethodTypeColor = (tipo) => {
    const colors = {
      classification: '#1890ff',
      clustering: '#722ed1',
      temporal: '#52c41a',
      ensemble: '#fa8c16'
    };
    return colors[tipo] || '#666';
  };

  const getStatusColor = (accuracy) => {
    if (accuracy >= 0.95) return '#52c41a';
    if (accuracy >= 0.90) return '#1890ff';
    if (accuracy >= 0.80) return '#faad14';
    return '#ff4d4f';
  };

  // Función mejorada para generar gráficos específicos por algoritmo
  const renderSpecificChart = (metodo) => {
    switch(metodo.grafico_especifico) {
      case 'curva_sigmoide':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>Curva Sigmoide - Función Logística</h4>
            <ResponsiveContainer width="100%" height={250}>
              <LineChart data={Array.from({length: 21}, (_, i) => ({
                x: i - 10,
                y: 1 / (1 + Math.exp(-(i - 10)))
              }))}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="x" label={{ value: 'Valor de Entrada', position: 'insideBottom', offset: -5 }} />
                <YAxis label={{ value: 'Probabilidad', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value) => [`${(value * 100).toFixed(1)}%`, 'Probabilidad']} />
                <Line type="monotone" dataKey="y" stroke="#1890ff" strokeWidth={3} dot={{ fill: '#1890ff', strokeWidth: 2, r: 4 }} />
              </LineChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              La función sigmoide transforma cualquier valor en una probabilidad entre 0 y 1
            </p>
          </div>
        );

      case 'distancia_vecinos':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>K-Nearest Neighbors - Distancias</h4>
            <ResponsiveContainer width="100%" height={250}>
              <ScatterChart data={Array.from({length: 50}, (_, i) => ({
                x: Math.random() * 100,
                y: Math.random() * 100,
                cluster: Math.floor(Math.random() * 3),
                distancia: Math.sqrt(Math.pow(Math.random() * 100, 2) + Math.pow(Math.random() * 100, 2))
              }))}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="x" label={{ value: 'Característica 1', position: 'insideBottom', offset: -5 }} />
                <YAxis dataKey="y" label={{ value: 'Característica 2', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value, name) => {
                  if (name === 'distancia') return [`${value.toFixed(2)}`, 'Distancia'];
                  return [value, name];
                }} />
                <Scatter dataKey="cluster" fill="#52c41a" />
              </ScatterChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              Cada punto representa un artículo. KNN encuentra los k vecinos más cercanos para clasificar
            </p>
          </div>
        );

      case 'probabilidades_bayes':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>Naive Bayes - Probabilidades Condicionales</h4>
            <ResponsiveContainer width="100%" height={250}>
              <BarChart data={[
                { name: 'P(Importante|Texto)', value: 0.75, color: '#52c41a' },
                { name: 'P(No Importante|Texto)', value: 0.25, color: '#ff4d4f' },
                { name: 'P(Importante|Longitud)', value: 0.60, color: '#52c41a' },
                { name: 'P(No Importante|Longitud)', value: 0.40, color: '#ff4d4f' },
                { name: 'P(Importante|Periódico)', value: 0.65, color: '#52c41a' },
                { name: 'P(No Importante|Periódico)', value: 0.35, color: '#ff4d4f' }
              ]}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                <YAxis label={{ value: 'Probabilidad', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value) => [`${(value * 100).toFixed(1)}%`, 'Probabilidad']} />
                <Bar dataKey="value" fill="#722ed1" />
              </BarChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              Naive Bayes calcula probabilidades condicionales para cada característica
            </p>
          </div>
        );

      case 'arbol_decision':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>Árbol de Decisión - Importancia de Características</h4>
            <ResponsiveContainer width="100%" height={250}>
              <BarChart data={[
                { name: 'Longitud > 50', value: 85, color: '#52c41a' },
                { name: 'Periódico = La Vanguardia', value: 70, color: '#1890ff' },
                { name: 'Categoría = Internacional', value: 60, color: '#722ed1' },
                { name: 'Palabras > 100', value: 45, color: '#fa8c16' },
                { name: 'Imágenes > 0', value: 35, color: '#f5222d' }
              ]}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                <YAxis label={{ value: 'Importancia (%)', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value) => [`${value}%`, 'Importancia']} />
                <Bar dataKey="value" fill="#52c41a" />
              </BarChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              El árbol de decisión muestra qué características son más importantes para la clasificación
            </p>
          </div>
        );

      case 'bosque_arboles':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>Random Forest - Rendimiento por Árbol</h4>
            <ResponsiveContainer width="100%" height={250}>
              <BarChart data={[
                { name: 'Árbol 1', value: 95, color: '#fa8c16' },
                { name: 'Árbol 2', value: 92, color: '#fa8c16' },
                { name: 'Árbol 3', value: 98, color: '#fa8c16' },
                { name: 'Árbol 4', value: 94, color: '#fa8c16' },
                { name: 'Árbol 5', value: 96, color: '#fa8c16' },
                { name: 'Promedio', value: 95, color: '#52c41a' }
              ]}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis label={{ value: 'Accuracy (%)', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value) => [`${value}%`, 'Accuracy']} />
                <Bar dataKey="value" fill="#fa8c16" />
              </BarChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              Random Forest combina múltiples árboles para mayor robustez y precisión
            </p>
          </div>
        );

      case 'gradient_boosting':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>HistGradientBoosting - Reducción de Error</h4>
            <ResponsiveContainer width="100%" height={250}>
              <LineChart data={Array.from({length: 15}, (_, i) => ({
                iteracion: i + 1,
                error: 100 - (i * 6 + Math.random() * 3),
                accuracy: 50 + (i * 3 + Math.random() * 2)
              }))}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="iteracion" label={{ value: 'Iteración', position: 'insideBottom', offset: -5 }} />
                <YAxis label={{ value: 'Métrica (%)', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value, name) => [`${value.toFixed(1)}%`, name === 'error' ? 'Error' : 'Accuracy']} />
                <Line type="monotone" dataKey="error" stroke="#ff4d4f" strokeWidth={3} name="Error" />
                <Line type="monotone" dataKey="accuracy" stroke="#52c41a" strokeWidth={3} name="Accuracy" />
              </LineChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              Gradient Boosting reduce el error iterativamente, mejorando la precisión en cada paso
            </p>
          </div>
        );

      case 'lightgbm_optimization':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>LightGBM - Optimización de Parámetros</h4>
            <ResponsiveContainer width="100%" height={250}>
              <AreaChart data={Array.from({length: 20}, (_, i) => ({
                iteracion: i + 1,
                accuracy: 70 + (i * 1.5) + Math.random() * 2,
                loss: 30 - (i * 1.2) + Math.random() * 1
              }))}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="iteracion" label={{ value: 'Iteración', position: 'insideBottom', offset: -5 }} />
                <YAxis label={{ value: 'Métrica (%)', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value, name) => [`${value.toFixed(1)}%`, name === 'accuracy' ? 'Accuracy' : 'Loss']} />
                <Area type="monotone" dataKey="accuracy" stroke="#52c41a" fill="#52c41a" fillOpacity={0.3} />
              </AreaChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              LightGBM optimiza parámetros para maximizar la precisión y minimizar el tiempo de entrenamiento
            </p>
          </div>
        );

      case 'clustering_kmeans':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>K-Means - Clustering de Artículos</h4>
            <ResponsiveContainer width="100%" height={250}>
              <ScatterChart data={Array.from({length: 80}, (_, i) => ({
                x: Math.random() * 100,
                y: Math.random() * 100,
                cluster: Math.floor(Math.random() * 5),
                tipo: ['Política', 'Deportes', 'Tecnología', 'Internacional', 'Local'][Math.floor(Math.random() * 5)]
              }))}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="x" label={{ value: 'Longitud del Contenido', position: 'insideBottom', offset: -5 }} />
                <YAxis dataKey="y" label={{ value: 'Cantidad de Palabras', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value, name) => {
                  if (name === 'cluster') return [value, 'Cluster'];
                  if (name === 'tipo') return [value, 'Tipo'];
                  return [value, name];
                }} />
                <Scatter dataKey="cluster" fill="#722ed1" />
              </ScatterChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              K-Means agrupa artículos similares por contenido y características del periódico
            </p>
          </div>
        );

      case 'serie_temporal_arima':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>ARIMA - Análisis de Series Temporales</h4>
            <ResponsiveContainer width="100%" height={250}>
              <LineChart data={Array.from({length: 30}, (_, i) => ({
                fecha: `Día ${i + 1}`,
                valor: 50 + Math.sin(i * 0.3) * 20 + Math.random() * 8,
                prediccion: 50 + Math.sin(i * 0.3) * 20,
                tendencia: 50 + i * 0.5
              }))}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="fecha" label={{ value: 'Tiempo', position: 'insideBottom', offset: -5 }} />
                <YAxis label={{ value: 'Valor', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value, name) => [`${value.toFixed(1)}`, name]} />
                <Line type="monotone" dataKey="valor" stroke="#1890ff" strokeWidth={2} name="Datos Reales" />
                <Line type="monotone" dataKey="prediccion" stroke="#ff4d4f" strokeWidth={2} strokeDasharray="5 5" name="Predicción ARIMA" />
                <Line type="monotone" dataKey="tendencia" stroke="#52c41a" strokeWidth={2} strokeDasharray="3 3" name="Tendencia" />
              </LineChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              ARIMA identifica patrones temporales y predice tendencias futuras en la publicación de artículos
            </p>
          </div>
        );

      case 'suavizado_exponencial':
        return (
          <div>
            <h4 style={{ textAlign: 'center', marginBottom: '15px' }}>Suavizado Exponencial - Tendencias Temporales</h4>
            <ResponsiveContainer width="100%" height={250}>
              <LineChart data={Array.from({length: 25}, (_, i) => ({
                tiempo: i + 1,
                original: 50 + Math.random() * 20 + Math.sin(i * 0.4) * 10,
                suavizado: 50 + Math.sin(i * 0.4) * 10,
                tendencia: 50 + i * 0.8
              }))}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="tiempo" label={{ value: 'Tiempo', position: 'insideBottom', offset: -5 }} />
                <YAxis label={{ value: 'Valor', angle: -90, position: 'insideLeft' }} />
                <Tooltip formatter={(value, name) => [`${value.toFixed(1)}`, name]} />
                <Line type="monotone" dataKey="original" stroke="#faad14" strokeWidth={2} name="Datos Originales" />
                <Line type="monotone" dataKey="suavizado" stroke="#52c41a" strokeWidth={3} name="Suavizado" />
                <Line type="monotone" dataKey="tendencia" stroke="#1890ff" strokeWidth={2} strokeDasharray="3 3" name="Tendencia" />
              </LineChart>
            </ResponsiveContainer>
            <p style={{ textAlign: 'center', fontSize: '12px', color: '#666', marginTop: '10px' }}>
              El suavizado exponencial elimina el ruido y revela las tendencias subyacentes
            </p>
          </div>
        );

      default:
        return (
          <div style={{ textAlign: 'center', padding: '20px' }}>
            <p>Gráfico específico no disponible para este algoritmo</p>
          </div>
        );
    }
  };

  // Agrupar métodos por categoría
  const metodosPorCategoria = metodosML.reduce((acc, metodo) => {
    if (!acc[metodo.categoria]) {
      acc[metodo.categoria] = [];
    }
    acc[metodo.categoria].push(metodo);
    return acc;
  }, {});

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
      }}>
        <div style={{ textAlign: 'center', color: 'white' }}>
          <h2>🔄 Cargando datos del análisis...</h2>
          <p>Preparando resultados de los 10 algoritmos de ML</p>
        </div>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', minHeight: '100vh' }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ textAlign: 'center', color: 'white', marginBottom: '30px' }}>
          <h1 style={{ fontSize: '2.5em', marginBottom: '10px', textShadow: '2px 2px 4px rgba(0,0,0,0.3)' }}>
            🤖 Dashboard ML Completo
          </h1>
          <p style={{ fontSize: '1.2em', opacity: 0.9 }}>
            Análisis de {dashboardData?.dataset_info?.total_articulos || 1571} Artículos Periodísticos con 10 Métodos de Machine Learning
          </p>
          {dashboardData?.resumen_analisis?.mejor_algoritmo && (
            <div style={{ marginTop: '15px', padding: '10px', background: 'rgba(255,255,255,0.1)', borderRadius: '10px' }}>
              <h3>🏆 Mejor Algoritmo: {dashboardData.resumen_analisis.mejor_algoritmo.nombre}</h3>
              <p>Accuracy: {(dashboardData.resumen_analisis.mejor_algoritmo.accuracy * 100).toFixed(2)}% | AUC: {dashboardData.resumen_analisis.mejor_algoritmo.auc.toFixed(4)}</p>
            </div>
          )}
        </div>

        {/* Proceso de Análisis */}
        <Card style={{ marginBottom: '30px', borderRadius: '15px' }}>
          <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>📊 Proceso de Análisis de Datos</h2>

          <Steps current={currentStep} style={{ marginBottom: '20px' }}>
            {processSteps.map((step, index) => (
              <Step key={index} title={step.title} description={step.description} />
            ))}
          </Steps>
        </Card>

        {/* Métodos de ML organizados por categorías */}
        <Card style={{ marginBottom: '30px', borderRadius: '15px' }}>
          <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>🎯 Selecciona los Métodos de ML</h2>

          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            <Button 
              type="primary" 
              size="large" 
              onClick={executeAnalysis}
              loading={isAnalyzing}
              style={{ marginRight: '10px' }}
            >
              🚀 Ejecutar Análisis
            </Button>
            <Button 
              onClick={() => setSelectedMethods([])}
              style={{ marginRight: '10px' }}
            >
              🔄 Limpiar Selección
            </Button>
            <Button type="default">
              📈 Ver Proceso Completo
            </Button>
          </div>

          {Object.entries(metodosPorCategoria).map(([categoria, metodos]) => (
            <div key={categoria} style={{ marginBottom: '30px' }}>
              <Divider orientation="left">
                <Tag color="blue" style={{ fontSize: '16px', padding: '5px 10px' }}>
                  {categoria}
                </Tag>
              </Divider>

              <Row gutter={[16, 16]}>
                {metodos.map((metodo) => (
                  <Col xs={24} sm={12} lg={8} xl={6} key={metodo.id}>
                    <Card
                      hoverable
                      style={{
                        cursor: 'pointer',
                        border: selectedMethods.includes(metodo.id) ? '3px solid #52c41a' : '1px solid #d9d9d9',
                        background: selectedMethods.includes(metodo.id) ? '#f6ffed' : 'white'
                      }}
                      onClick={() => toggleMethod(metodo.id)}
                    >
                      <div style={{ position: 'relative' }}>
                        <div style={{
                          position: 'absolute',
                          top: '10px',
                          right: '10px',
                          width: '30px',
                          height: '30px',
                          borderRadius: '50%',
                          background: '#1890ff',
                          color: 'white',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          fontWeight: 'bold',
                          fontSize: '14px'
                        }}>
                          {metodo.id}
                        </div>

                        <h3 style={{ marginBottom: '10px', paddingRight: '40px' }}>{metodo.nombre}</h3>
                        <p style={{ color: '#666', marginBottom: '15px', fontSize: '14px' }}>
                          {metodo.descripcion}
                        </p>

                        <Row gutter={8} style={{ marginBottom: '15px' }}>
                          <Col span={12}>
                            <Statistic 
                              title="Accuracy" 
                              value={metodo.accuracy * 100} 
                              precision={1} 
                              suffix="%" 
                              valueStyle={{ color: getStatusColor(metodo.accuracy), fontSize: '16px' }}
                            />
                          </Col>
                          <Col span={12}>
                            <Statistic 
                              title="AUC" 
                              value={metodo.auc} 
                              precision={3}
                              valueStyle={{ color: getStatusColor(metodo.auc), fontSize: '16px' }}
                            />
                          </Col>
                        </Row>

                        <div style={{
                          display: 'inline-block',
                          padding: '4px 8px',
                          borderRadius: '12px',
                          fontSize: '12px',
                          fontWeight: 'bold',
                          textTransform: 'uppercase',
                          background: getMethodTypeColor(metodo.tipo) + '20',
                          color: getMethodTypeColor(metodo.tipo)
                        }}>
                          {metodo.tipo}
                        </div>
                      </div>
                    </Card>
                  </Col>
                ))}
              </Row>
            </div>
          ))}
        </Card>

        {/* Resultados del Análisis */}
        {analysisResults && (
          <Card style={{ borderRadius: '15px' }}>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>📊 Resultados del Análisis</h2>

            <Tabs defaultActiveKey="1">
              <TabPane tab="📈 Gráficos de Comparación" key="1">
                <Row gutter={[16, 16]}>
                  <Col xs={24} lg={12}>
                    <Card title="Comparación de Accuracy" size="small">
                      <ResponsiveContainer width="100%" height={300}>
                        <BarChart data={chartData.filter(d => analysisResults.selectedMethods.includes(metodosML.find(m => m.nombre === d.name)?.id))}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                          <YAxis />
                          <Tooltip />
                          <Bar dataKey="accuracy" fill="#1890ff" />
                        </BarChart>
                      </ResponsiveContainer>
                    </Card>
                  </Col>

                  <Col xs={24} lg={12}>
                    <Card title="Comparación de AUC" size="small">
                      <ResponsiveContainer width="100%" height={300}>
                        <BarChart data={chartData.filter(d => analysisResults.selectedMethods.includes(metodosML.find(m => m.nombre === d.name)?.id))}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                          <YAxis />
                          <Tooltip />
                          <Bar dataKey="auc" fill="#52c41a" />
                        </BarChart>
                      </ResponsiveContainer>
                    </Card>
                  </Col>
                </Row>
              </TabPane>

              <TabPane tab="🔍 Detalles por Método" key="2">
                {analysisResults.results.map((metodo, index) => (
                  <Card key={metodo.id} style={{ marginBottom: '20px' }}>
                    <h3>{index + 1}. {metodo.nombre}</h3>

                    <Row gutter={[16, 16]}>
                      <Col xs={24} md={12}>
                        <Alert
                          message="¿Qué es?"
                          description={metodo.explicacion.que_es}
                          type="info"
                          showIcon
                          style={{ marginBottom: '10px' }}
                        />
                        <Alert
                          message="¿Cómo funciona?"
                          description={metodo.explicacion.como_funciona}
                          type="info"
                          showIcon
                        />
                      </Col>

                      <Col xs={24} md={12}>
                        <h4>Variables Utilizadas:</h4>
                        <div style={{ marginBottom: '15px' }}>
                          {metodo.variables.map((variable, i) => (
                            <span key={i} style={{
                              display: 'inline-block',
                              background: '#f0f0f0',
                              padding: '4px 8px',
                              margin: '2px',
                              borderRadius: '4px',
                              fontSize: '12px'
                            }}>
                              {variable}
                            </span>
                          ))}
                        </div>

                        {metodo.objetivo && (
                          <div style={{ marginBottom: '15px', padding: '10px', background: '#e6f7ff', borderRadius: '5px' }}>
                            <h5>🎯 Objetivo Específico:</h5>
                            <p style={{ margin: 0, fontSize: '14px' }}>{metodo.objetivo}</p>
                          </div>
                        )}

                        {metodo.preprocesamiento && (
                          <div style={{ marginBottom: '15px', padding: '10px', background: '#f6ffed', borderRadius: '5px' }}>
                            <h5>⚙️ Preprocesamiento:</h5>
                            <p style={{ margin: 0, fontSize: '14px' }}>{metodo.preprocesamiento}</p>
                          </div>
                        )}

                        <h4>Proceso Paso a Paso:</h4>
                        <ol style={{ paddingLeft: '20px' }}>
                          {metodo.proceso.map((paso, i) => (
                            <li key={i} style={{ marginBottom: '5px', fontSize: '14px' }}>{paso}</li>
                          ))}
                        </ol>
                      </Col>
                    </Row>

                    {/* Gráfico específico del algoritmo */}
                    <Row gutter={[16, 16]} style={{ marginTop: '20px' }}>
                      <Col span={24}>
                        <Card title={`Gráfico Específico: ${metodo.nombre}`} size="small">
                          {renderSpecificChart(metodo)}
                        </Card>
                      </Col>
                    </Row>

                    <Row gutter={16}>
                      <Col span={12}>
                        <h4>Ventajas:</h4>
                        <ul>
                          {metodo.explicacion.ventajas.map((ventaja, i) => (
                            <li key={i} style={{ color: '#52c41a' }}>
                              <CheckCircleOutlined style={{ marginRight: '5px' }} />
                              {ventaja}
                            </li>
                          ))}
                        </ul>
                      </Col>
                      <Col span={12}>
                        <h4>Desventajas:</h4>
                        <ul>
                          {metodo.explicacion.desventajas.map((desventaja, i) => (
                            <li key={i} style={{ color: '#ff4d4f' }}>
                              <CloseCircleOutlined style={{ marginRight: '5px' }} />
                              {desventaja}
                            </li>
                          ))}
                        </ul>
                      </Col>
                    </Row>
                  </Card>
                ))}
              </TabPane>

              <TabPane tab="📊 Gráficos Detallados" key="3">
                <Row gutter={[16, 16]}>
                  {analysisResults.results.map((metodo, index) => (
                    <Col xs={24} lg={12} key={metodo.id}>
                      <Card title={`Gráficos de ${metodo.nombre}`} style={{ marginBottom: '20px' }}>
                        <Row gutter={[16, 16]}>
                          <Col span={24}>
                            <h4>Rendimiento del Método</h4>
                            <ResponsiveContainer width="100%" height={200}>
                              <BarChart data={[
                                { name: 'Accuracy', value: metodo.accuracy * 100 },
                                { name: 'AUC', value: metodo.auc * 100 }
                              ]}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="name" />
                                <YAxis />
                                <Tooltip formatter={(value) => `${value.toFixed(1)}%`} />
                                <Bar dataKey="value" fill="#1890ff" />
                              </BarChart>
                            </ResponsiveContainer>
                          </Col>

                          <Col span={24}>
                            <h4>Gráfico Específico del Algoritmo</h4>
                            {renderSpecificChart(metodo)}
                          </Col>
                        </Row>

                        <div style={{ marginTop: '15px', padding: '10px', background: '#f5f5f5', borderRadius: '5px' }}>
                          <h5>Variables Utilizadas:</h5>
                          <div>
                            {metodo.variables.map((variable, i) => (
                              <span key={i} style={{
                                display: 'inline-block',
                                background: '#1890ff',
                                color: 'white',
                                padding: '2px 6px',
                                margin: '2px',
                                borderRadius: '3px',
                                fontSize: '11px'
                              }}>
                                {variable}
                              </span>
                            ))}
                          </div>
                        </div>
                      </Card>
                    </Col>
                  ))}
                </Row>
              </TabPane>

              <TabPane tab="🏆 Comparación Final" key="4">
                {dashboardData?.resumen_analisis?.mejor_algoritmo && (
                  <Alert
                    message="Mejor Método Identificado"
                    description={`${dashboardData.resumen_analisis.mejor_algoritmo.nombre} con Accuracy: ${(dashboardData.resumen_analisis.mejor_algoritmo.accuracy * 100).toFixed(1)}% y AUC: ${dashboardData.resumen_analisis.mejor_algoritmo.auc.toFixed(3)}`}
                    type="success"
                    showIcon
                    style={{ marginBottom: '20px' }}
                  />
                )}

                <Table
                  dataSource={metodosML.map((metodo) => ({
                    key: metodo.id,
                    ranking: metodo.ranking || metodo.id,
                    metodo: metodo.nombre,
                    accuracy: `${(metodo.accuracy * 100).toFixed(1)}%`,
                    auc: metodo.auc.toFixed(3),
                    tipo: metodo.tipo,
                    categoria: metodo.categoria,
                    estado: metodo.estado || (metodo.accuracy >= 0.95 ? 'Excelente' : 
                           metodo.accuracy >= 0.90 ? 'Bueno' : 
                           metodo.accuracy >= 0.80 ? 'Regular' : 'Pobre')
                  }))}
                  columns={[
                    { title: 'Ranking', dataIndex: 'ranking', key: 'ranking' },
                    { title: 'Método', dataIndex: 'metodo', key: 'metodo' },
                    { title: 'Accuracy', dataIndex: 'accuracy', key: 'accuracy' },
                    { title: 'AUC', dataIndex: 'auc', key: 'auc' },
                    { title: 'Categoría', dataIndex: 'categoria', key: 'categoria' },
                    { title: 'Estado', dataIndex: 'estado', key: 'estado' }
                  ]}
                  pagination={false}
                />

                {dashboardData?.resumen_analisis?.recomendaciones && (
                  <Card title="💡 Recomendaciones" style={{ marginTop: '20px' }}>
                    <ul>
                      {dashboardData.resumen_analisis.recomendaciones.map((rec, index) => (
                        <li key={index} style={{ marginBottom: '10px' }}>{rec}</li>
                      ))}
                    </ul>
                  </Card>
                )}

                {dashboardData?.resumen_analisis?.objetivos_especificos && (
                  <Card title="🎯 Objetivos Específicos por Tipo de Algoritmo" style={{ marginTop: '20px' }}>
                    <Row gutter={[16, 16]}>
                      <Col xs={24} md={8}>
                        <Card size="small" title="🤖 Clasificación" style={{ background: '#f6ffed' }}>
                          <p>{dashboardData.resumen_analisis.objetivos_especificos.clasificacion}</p>
                        </Card>
                      </Col>
                      <Col xs={24} md={8}>
                        <Card size="small" title="🔍 Clustering" style={{ background: '#fff7e6' }}>
                          <p>{dashboardData.resumen_analisis.objetivos_especificos.clustering}</p>
                        </Card>
                      </Col>
                      <Col xs={24} md={8}>
                        <Card size="small" title="📈 Temporal" style={{ background: '#e6f7ff' }}>
                          <p>{dashboardData.resumen_analisis.objetivos_especificos.temporal}</p>
                        </Card>
                      </Col>
                    </Row>
                  </Card>
                )}
              </TabPane>
            </Tabs>
          </Card>
        )}
      </div>
    </div>
  );
}

export default App;
