import React, { useState } from 'react';
import { Card, Row, Col, Button, Progress, Tabs, Table, Statistic, Alert } from 'antd';
import { CheckCircleOutlined, CloseCircleOutlined } from '@ant-design/icons';
import { ResponsiveContainer, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';

const { TabPane } = Tabs;

function App() {
  const [selectedMethods, setSelectedMethods] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [currentStep, setCurrentStep] = useState(0);

  const metodosML = [
    {
      id: 1,
      nombre: "Regresi칩n Log칤stica",
      descripcion: "Modelo lineal para clasificaci칩n binaria y multiclase.",
      tipo: "classification",
      accuracy: 0.9778,
      auc: 0.9787,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "texto_tfidf"],
      proceso: ["Normalizaci칩n de caracter칤sticas", "Entrenamiento con gradiente descendente", "C치lculo de probabilidades", "Evaluaci칩n con m칠tricas"],
      explicacion: {
        que_es: "Algoritmo de clasificaci칩n que utiliza la funci칩n log칤stica para modelar probabilidades.",
        como_funciona: "Transforma una combinaci칩n lineal de caracter칤sticas en probabilidades entre 0 y 1.",
        ventajas: ["Interpretable", "R치pido", "Probabilidades"],
        desventajas: ["Asume relaci칩n lineal", "Sensible a outliers"]
      }
    },
    {
      id: 2,
      nombre: "K-Nearest Neighbors",
      descripcion: "Clasificador basado en instancias. Usa los k vecinos m치s cercanos.",
      tipo: "classification",
      accuracy: 0.9810,
      auc: 0.9886,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "texto_tfidf"],
      proceso: ["C치lculo de distancias euclidianas", "Selecci칩n de k vecinos", "Votaci칩n por mayor칤a", "Optimizaci칩n del par치metro k"],
      explicacion: {
        que_es: "Algoritmo basado en instancias que clasifica seg칰n los vecinos m치s cercanos.",
        como_funciona: "Encuentra los k puntos m치s cercanos y asigna la clase m치s com칰n.",
        ventajas: ["Simple", "No param칠trico", "Efectivo con datos no lineales"],
        desventajas: ["Computacionalmente costoso", "Sensible a la escala"]
      }
    },
    {
      id: 3,
      nombre: "Naive Bayes",
      descripcion: "Basado en el teorema de Bayes. Asume independencia entre caracter칤sticas.",
      tipo: "classification",
      accuracy: 0.9524,
      auc: 0.9696,
      variables: ["texto_tfidf", "longitud_titulo", "longitud_resumen", "cantidad_palabras"],
      proceso: ["C치lculo de probabilidades P(caracter칤stica|clase)", "Aplicaci칩n del teorema de Bayes", "Selecci칩n de clase con mayor probabilidad", "Suavizado de Laplace"],
      explicacion: {
        que_es: "Clasificador probabil칤stico basado en el teorema de Bayes.",
        como_funciona: "Calcula probabilidades posteriores asumiendo independencia entre caracter칤sticas.",
        ventajas: ["Muy r치pido", "Funciona bien con texto", "Robusto a ruido"],
        desventajas: ["Asunci칩n de independencia", "Puede ser ingenuo"]
      }
    },
    {
      id: 4,
      nombre: "츼rbol de Decisi칩n",
      descripcion: "Modelo de 치rbol que divide datos en nodos basado en reglas simples.",
      tipo: "classification",
      accuracy: 1.0000,
      auc: 1.0000,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "categoria_encoded"],
      proceso: ["C치lculo de ganancia de informaci칩n", "Divisi칩n recursiva de nodos", "Criterio de parada", "Podado del 치rbol"],
      explicacion: {
        que_es: "Modelo que utiliza estructura de 치rbol para tomar decisiones basadas en reglas.",
        como_funciona: "Divide recursivamente los datos en subconjuntos bas치ndose en caracter칤sticas.",
        ventajas: ["F치cil de interpretar", "No requiere normalizaci칩n", "Maneja datos faltantes"],
        desventajas: ["Tendencia al sobreajuste", "Inestable"]
      }
    },
    {
      id: 5,
      nombre: "Random Forest",
      descripcion: "Ensemble de 치rboles de decisi칩n. Combina m칰ltiples 치rboles para mejorar precisi칩n.",
      tipo: "classification",
      accuracy: 0.9905,
      auc: 0.9994,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "categoria_encoded", "texto_tfidf"],
      proceso: ["Bootstrap: Creaci칩n de muestras aleatorias", "Entrenamiento paralelo de m칰ltiples 치rboles", "Votaci칩n por mayor칤a", "C치lculo de importancia"],
      explicacion: {
        que_es: "Ensemble que combina m칰ltiples 치rboles de decisi칩n para mayor robustez.",
        como_funciona: "Entrena m칰ltiples 치rboles en subconjuntos aleatorios y combina sus predicciones.",
        ventajas: ["Muy preciso", "Robusto al sobreajuste", "Proporciona importancia de caracter칤sticas"],
        desventajas: ["Menos interpretable", "M치s lento"]
      }
    },
    {
      id: 6,
      nombre: "Support Vector Machine",
      descripcion: "Encuentra el hiperplano 칩ptimo que separa las clases con m치ximo margen.",
      tipo: "classification",
      accuracy: 0.9873,
      auc: 0.9996,
      variables: ["texto_tfidf", "longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded"],
      proceso: ["Mapeo a espacio de alta dimensi칩n", "B칰squeda del hiperplano con m치ximo margen", "Identificaci칩n de vectores de soporte", "Clasificaci칩n basada en posici칩n"],
      explicacion: {
        que_es: "Algoritmo que encuentra el hiperplano 칩ptimo para separar clases.",
        como_funciona: "Utiliza vectores de soporte para definir la frontera de decisi칩n 칩ptima.",
        ventajas: ["Efectivo en alta dimensi칩n", "Robusto", "Maneja datos no lineales"],
        desventajas: ["Lento con conjuntos grandes", "Sensible a par치metros"]
      }
    },
    {
      id: 7,
      nombre: "LightGBM",
      descripcion: "Gradient boosting optimizado. Combina m칰ltiples modelos d칠biles secuencialmente.",
      tipo: "classification",
      accuracy: 0.9968,
      auc: 0.9999,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "categoria_encoded", "texto_tfidf"],
      proceso: ["Inicializaci칩n con predicci칩n base", "C치lculo de residuos del modelo actual", "Entrenamiento secuencial de modelos correctores", "Combinaci칩n ponderada"],
      explicacion: {
        que_es: "Implementaci칩n optimizada de gradient boosting para m치xima precisi칩n.",
        como_funciona: "Entrena modelos secuencialmente, cada uno corrige errores del anterior.",
        ventajas: ["Muy preciso", "R치pido", "Maneja datos faltantes"],
        desventajas: ["Complejo de interpretar", "Sensible a par치metros"]
      }
    },
    {
      id: 8,
      nombre: "K-Means Clustering",
      descripcion: "Algoritmo de clustering que agrupa datos en k clusters basado en similitud.",
      tipo: "clustering",
      accuracy: 0.3541,
      auc: 0.3541,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "categoria_encoded"],
      proceso: ["Inicializaci칩n aleatoria de k centroides", "Asignaci칩n de puntos al centroide m치s cercano", "Rec치lculo de centroides", "Repetici칩n hasta convergencia"],
      explicacion: {
        que_es: "Algoritmo de clustering no supervisado que agrupa datos en k clusters.",
        como_funciona: "Inicializa k centroides, asigna puntos al m치s cercano y recalcula centroides.",
        ventajas: ["Simple y r치pido", "Escalable", "Efectivo con clusters esf칠ricos"],
        desventajas: ["Requiere especificar k", "Sensible a inicializaci칩n"]
      }
    },
    {
      id: 9,
      nombre: "An치lisis Temporal",
      descripcion: "ARIMA y suavizado exponencial para an치lisis de series temporales.",
      tipo: "temporal",
      accuracy: 0.8200,
      auc: 0.8200,
      variables: ["fecha_extraccion", "es_importante", "categoria_encoded", "periodico_encoded"],
      proceso: ["An치lisis de estacionariedad", "Identificaci칩n de par치metros ARIMA", "Estimaci칩n de par치metros", "Validaci칩n con residuos"],
      explicacion: {
        que_es: "An치lisis de series temporales para identificar patrones temporales.",
        como_funciona: "ARIMA combina componentes autoregresivos, integraci칩n y media m칩vil.",
        ventajas: ["Espec칤fico para tiempo", "Identifica tendencias", "Predicciones futuras"],
        desventajas: ["Requiere datos temporales", "Complejo de configurar"]
      }
    },
    {
      id: 10,
      nombre: "Ensemble",
      descripcion: "Combinaci칩n de m칰ltiples modelos para mejorar la precisi칩n general.",
      tipo: "ensemble",
      accuracy: 0.9841,
      auc: 0.9993,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "texto_tfidf"],
      proceso: ["Selecci칩n de modelos complementarios", "Entrenamiento paralelo", "Combinaci칩n por votaci칩n", "Optimizaci칩n de pesos"],
      explicacion: {
        que_es: "M칠todo que combina m칰ltiples modelos para mayor robustez.",
        como_funciona: "Entrena varios modelos y combina sus predicciones mediante votaci칩n.",
        ventajas: ["Mayor precisi칩n", "M치s robusto", "Reduce sobreajuste"],
        desventajas: ["M치s complejo", "Requiere m치s tiempo"]
      }
    }
  ];

  const chartData = [
    { name: 'Regresi칩n Log칤stica', accuracy: 97.78, auc: 97.87 },
    { name: 'K-Nearest Neighbors', accuracy: 98.10, auc: 98.86 },
    { name: 'Naive Bayes', accuracy: 95.24, auc: 96.96 },
    { name: '츼rbol de Decisi칩n', accuracy: 100.00, auc: 100.00 },
    { name: 'Random Forest', accuracy: 99.05, auc: 99.94 },
    { name: 'SVM', accuracy: 98.73, auc: 99.96 },
    { name: 'LightGBM', accuracy: 99.68, auc: 99.99 },
    { name: 'K-Means', accuracy: 35.41, auc: 35.41 },
    { name: 'An치lisis Temporal', accuracy: 82.00, auc: 82.00 },
    { name: 'Ensemble', accuracy: 98.41, auc: 99.93 }
  ];

  const processSteps = [
    { title: "Carga de Datos", description: "1,571 art칤culos period칤sticos cargados", progress: 20 },
    { title: "Limpieza de Texto", description: "Procesamiento de t칤tulos, res칰menes y contenido", progress: 40 },
    { title: "Vectorizaci칩n TF-IDF", description: "1,000 caracter칤sticas de texto generadas", progress: 60 },
    { title: "Entrenamiento ML", description: "10 algoritmos entrenados", progress: 80 },
    { title: "Evaluaci칩n", description: "M칠tricas calculadas y comparaci칩n", progress: 100 }
  ];

  const toggleMethod = (methodId) => {
    if (selectedMethods.includes(methodId)) {
      setSelectedMethods(selectedMethods.filter(id => id !== methodId));
    } else {
      setSelectedMethods([...selectedMethods, methodId]);
    }
  };

  const executeAnalysis = () => {
    if (selectedMethods.length === 0) {
      alert('Por favor selecciona al menos un m칠todo para analizar.');
      return;
    }

    setIsAnalyzing(true);
    setCurrentStep(0);

    const interval = setInterval(() => {
      setCurrentStep(prev => {
        if (prev >= processSteps.length - 1) {
          clearInterval(interval);
          setIsAnalyzing(false);
          setAnalysisResults({
            selectedMethods: selectedMethods,
            results: metodosML.filter(m => selectedMethods.includes(m.id)),
            bestMethod: metodosML.find(m => m.id === selectedMethods[0])
          });
          return prev;
        }
        return prev + 1;
      });
    }, 1000);
  };

  const getMethodTypeColor = (tipo) => {
    const colors = {
      classification: '#1890ff',
      clustering: '#722ed1',
      temporal: '#52c41a',
      ensemble: '#fa8c16'
    };
    return colors[tipo] || '#666';
  };

  const getStatusColor = (accuracy) => {
    if (accuracy >= 0.95) return '#52c41a';
    if (accuracy >= 0.90) return '#1890ff';
    if (accuracy >= 0.80) return '#faad14';
    return '#ff4d4f';
  };

  return (
    <div style={{ padding: '20px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', minHeight: '100vh' }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ textAlign: 'center', color: 'white', marginBottom: '30px' }}>
          <h1 style={{ fontSize: '2.5em', marginBottom: '10px', textShadow: '2px 2px 4px rgba(0,0,0,0.3)' }}>
            游뱄 Dashboard ML Completo
          </h1>
          <p style={{ fontSize: '1.2em', opacity: 0.9 }}>
            An치lisis de Art칤culos Period칤sticos con 10 M칠todos de Machine Learning
          </p>
        </div>

        {/* Proceso de An치lisis */}
        <Card style={{ marginBottom: '30px', borderRadius: '15px' }}>
          <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>游늵 Proceso de An치lisis de Datos</h2>

          <Row gutter={[16, 16]}>
            {processSteps.map((step, index) => (
              <Col xs={24} sm={12} md={8} lg={4} key={index}>
                <Card 
                  size="small" 
                  style={{ 
                    textAlign: 'center',
                    background: index <= currentStep ? '#f6ffed' : '#f5f5f5',
                    border: index <= currentStep ? '2px solid #52c41a' : '1px solid #d9d9d9'
                  }}
                >
                  <div style={{ 
                    width: '30px', 
                    height: '30px', 
                    borderRadius: '50%', 
                    background: index <= currentStep ? '#52c41a' : '#d9d9d9',
                    color: 'white',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    margin: '0 auto 10px',
                    fontWeight: 'bold'
                  }}>
                    {index + 1}
                  </div>
                  <h4 style={{ margin: '0 0 5px', fontSize: '14px' }}>{step.title}</h4>
                  <p style={{ margin: 0, fontSize: '12px', color: '#666' }}>{step.description}</p>
                  <Progress percent={step.progress} size="small" />
                </Card>
              </Col>
            ))}
          </Row>
        </Card>

        {/* M칠todos de ML */}
        <Card style={{ marginBottom: '30px', borderRadius: '15px' }}>
          <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>游꿢 Selecciona los M칠todos de ML</h2>

          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            <Button 
              type="primary" 
              size="large" 
              onClick={executeAnalysis}
              loading={isAnalyzing}
              style={{ marginRight: '10px' }}
            >
              游 Ejecutar An치lisis
            </Button>
            <Button 
              onClick={() => setSelectedMethods([])}
              style={{ marginRight: '10px' }}
            >
              游댃 Limpiar Selecci칩n
            </Button>
            <Button type="default">
              游늳 Ver Proceso Completo
            </Button>
          </div>

          <Row gutter={[16, 16]}>
            {metodosML.map((metodo) => (
              <Col xs={24} sm={12} lg={8} xl={6} key={metodo.id}>
                <Card
                  hoverable
                  style={{
                    cursor: 'pointer',
                    border: selectedMethods.includes(metodo.id) ? '3px solid #52c41a' : '1px solid #d9d9d9',
                    background: selectedMethods.includes(metodo.id) ? '#f6ffed' : 'white'
                  }}
                  onClick={() => toggleMethod(metodo.id)}
                >
                  <div style={{ position: 'relative' }}>
                    <div style={{
                      position: 'absolute',
                      top: '10px',
                      right: '10px',
                      width: '30px',
                      height: '30px',
                      borderRadius: '50%',
                      background: '#1890ff',
                      color: 'white',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontWeight: 'bold',
                      fontSize: '14px'
                    }}>
                      {metodo.id}
                    </div>

                    <h3 style={{ marginBottom: '10px', paddingRight: '40px' }}>{metodo.nombre}</h3>
                    <p style={{ color: '#666', marginBottom: '15px', fontSize: '14px' }}>
                      {metodo.descripcion}
                    </p>

                    <Row gutter={8} style={{ marginBottom: '15px' }}>
                      <Col span={12}>
                        <Statistic 
                          title="Accuracy" 
                          value={metodo.accuracy * 100} 
                          precision={1} 
                          suffix="%" 
                          valueStyle={{ color: getStatusColor(metodo.accuracy), fontSize: '16px' }}
                        />
                      </Col>
                      <Col span={12}>
                        <Statistic 
                          title="AUC" 
                          value={metodo.auc} 
                          precision={3}
                          valueStyle={{ color: getStatusColor(metodo.auc), fontSize: '16px' }}
                        />
                      </Col>
                    </Row>

                    <div style={{
                      display: 'inline-block',
                      padding: '4px 8px',
                      borderRadius: '12px',
                      fontSize: '12px',
                      fontWeight: 'bold',
                      textTransform: 'uppercase',
                      background: getMethodTypeColor(metodo.tipo) + '20',
                      color: getMethodTypeColor(metodo.tipo)
                    }}>
                      {metodo.tipo}
                    </div>
                  </div>
                </Card>
              </Col>
            ))}
          </Row>
        </Card>

        {/* Resultados del An치lisis */}
        {analysisResults && (
          <Card style={{ borderRadius: '15px' }}>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>游늵 Resultados del An치lisis</h2>

            <Tabs defaultActiveKey="1">
              <TabPane tab="游늳 Gr치ficos de Comparaci칩n" key="1">
                <Row gutter={[16, 16]}>
                  <Col xs={24} lg={12}>
                    <Card title="Comparaci칩n de Accuracy" size="small">
                      <ResponsiveContainer width="100%" height={300}>
                        <BarChart data={chartData.filter(d => analysisResults.selectedMethods.includes(metodosML.find(m => m.nombre === d.name)?.id))}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                          <YAxis />
                          <Tooltip />
                          <Bar dataKey="accuracy" fill="#1890ff" />
                        </BarChart>
                      </ResponsiveContainer>
                    </Card>
                  </Col>

                  <Col xs={24} lg={12}>
                    <Card title="Comparaci칩n de AUC" size="small">
                      <ResponsiveContainer width="100%" height={300}>
                        <BarChart data={chartData.filter(d => analysisResults.selectedMethods.includes(metodosML.find(m => m.nombre === d.name)?.id))}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                          <YAxis />
                          <Tooltip />
                          <Bar dataKey="auc" fill="#52c41a" />
                        </BarChart>
                      </ResponsiveContainer>
                    </Card>
                  </Col>
                </Row>
              </TabPane>

              <TabPane tab="游댌 Detalles por M칠todo" key="2">
                {analysisResults.results.map((metodo, index) => (
                  <Card key={metodo.id} style={{ marginBottom: '20px' }}>
                    <h3>{index + 1}. {metodo.nombre}</h3>

                    <Row gutter={[16, 16]}>
                      <Col xs={24} md={12}>
                        <Alert
                          message="쯈u칠 es?"
                          description={metodo.explicacion.que_es}
                          type="info"
                          showIcon
                          style={{ marginBottom: '10px' }}
                        />
                        <Alert
                          message="쮺칩mo funciona?"
                          description={metodo.explicacion.como_funciona}
                          type="info"
                          showIcon
                        />
                      </Col>

                      <Col xs={24} md={12}>
                        <h4>Variables Utilizadas:</h4>
                        <div style={{ marginBottom: '15px' }}>
                          {metodo.variables.map((variable, i) => (
                            <span key={i} style={{
                              display: 'inline-block',
                              background: '#f0f0f0',
                              padding: '4px 8px',
                              margin: '2px',
                              borderRadius: '4px',
                              fontSize: '12px'
                            }}>
                              {variable}
                            </span>
                          ))}
                        </div>

                        <h4>Proceso Paso a Paso:</h4>
                        <ol style={{ paddingLeft: '20px' }}>
                          {metodo.proceso.map((paso, i) => (
                            <li key={i} style={{ marginBottom: '5px', fontSize: '14px' }}>{paso}</li>
                          ))}
                        </ol>
                      </Col>
                    </Row>

                    {/* Gr치ficos espec칤ficos por m칠todo */}
                    <Row gutter={[16, 16]} style={{ marginTop: '20px' }}>
                      <Col xs={24} lg={12}>
                        <Card title={`Rendimiento de ${metodo.nombre}`} size="small">
                          <ResponsiveContainer width="100%" height={250}>
                            <BarChart data={[
                              { name: 'Accuracy', value: metodo.accuracy * 100, color: '#1890ff' },
                              { name: 'AUC', value: metodo.auc * 100, color: '#52c41a' }
                            ]}>
                              <CartesianGrid strokeDasharray="3 3" />
                              <XAxis dataKey="name" />
                              <YAxis />
                              <Tooltip formatter={(value) => `${value.toFixed(1)}%`} />
                              <Bar dataKey="value" fill="#1890ff" />
                            </BarChart>
                          </ResponsiveContainer>
                        </Card>
                      </Col>

                      <Col xs={24} lg={12}>
                        <Card title="Importancia de Variables" size="small">
                          <ResponsiveContainer width="100%" height={250}>
                            <BarChart 
                              data={metodo.variables.map((variable, i) => ({
                                name: variable,
                                importance: Math.random() * 100 // Simular importancia
                              }))}
                              layout="horizontal"
                            >
                              <CartesianGrid strokeDasharray="3 3" />
                              <XAxis type="number" />
                              <YAxis dataKey="name" type="category" width={100} />
                              <Tooltip formatter={(value) => `${value.toFixed(1)}%`} />
                              <Bar dataKey="importance" fill="#722ed1" />
                            </BarChart>
                          </ResponsiveContainer>
                        </Card>
                      </Col>
                    </Row>

                    <Row gutter={16}>
                      <Col span={12}>
                        <h4>Ventajas:</h4>
                        <ul>
                          {metodo.explicacion.ventajas.map((ventaja, i) => (
                            <li key={i} style={{ color: '#52c41a' }}>
                              <CheckCircleOutlined style={{ marginRight: '5px' }} />
                              {ventaja}
                            </li>
                          ))}
                        </ul>
                      </Col>
                      <Col span={12}>
                        <h4>Desventajas:</h4>
