                            {metodo.variables.map((variable, i) => (
                              <span key={i} style={{
                                display: 'inline-block',
                                background: '#1890ff',
                                color: 'white',
                                padding: '2px 6px',
                                margin: '2px',
                                borderRadius: '3px',
