                </Row>
              </TabPane>

              <TabPane tab="游끥 Comparaci칩n Final" key="4">
                <Alert
                  message="Mejor M칠todo Identificado"
                  description={`${analysisResults.bestMethod.nombre} con Accuracy: ${(analysisResults.bestMethod.accuracy * 100).toFixed(1)}% y AUC: ${analysisResults.bestMethod.auc.toFixed(3)}`}
                  type="success"
                  showIcon
                  style={{ marginBottom: '20px' }}
                />

                <Table
                  dataSource={analysisResults.results.map((metodo, index) => ({
                    key: metodo.id,
                    ranking: index + 1,
                    metodo: metodo.nombre,
                    accuracy: `${(metodo.accuracy * 100).toFixed(1)}%`,
                    auc: metodo.auc.toFixed(3),
