import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Button, Progress, Tabs, Table, Statistic, Alert, Divider, Tag } from 'antd';
import { CheckCircleOutlined, CloseCircleOutlined, InfoCircleOutlined } from '@ant-design/icons';
import { ResponsiveContainer, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, LineChart, Line, PieChart, Pie, Cell, ScatterChart, Scatter, AreaChart, Area } from 'recharts';

const { TabPane } = Tabs;

function App() {
  const [selectedMethods, setSelectedMethods] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [currentStep, setCurrentStep] = useState(0);
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);

  // Cargar datos reales del servidor
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await fetch('/api/dashboard-data');
        const data = await response.json();
        setDashboardData(data);
        setLoading(false);
      } catch (error) {
        console.error('Error cargando datos:', error);
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  // Usar datos reales del servidor o datos por defecto
  const metodosML = dashboardData?.resultados || [
    // 🤖 ALGORITMOS DE CLASIFICACIÓN (7 métodos)
    {
      id: 1,
      nombre: "Regresión Logística",
      descripcion: "Modelo base para comparación. Útil para problemas lineales.",
      tipo: "classification",
      categoria: "🤖 ALGORITMOS DE CLASIFICACIÓN",
      accuracy: 0.9778,
      auc: 0.9787,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "texto_tfidf"],
      proceso: [
        "Normalización de características numéricas",
        "Entrenamiento con gradiente descendente", 
        "Cálculo de probabilidades con función sigmoide",
        "Evaluación con métricas de precisión"
      ],
      explicacion: {
        que_es: "Algoritmo de clasificación que utiliza la función logística para modelar probabilidades.",
        como_funciona: "Transforma una combinación lineal de características en probabilidades entre 0 y 1.",
        ventajas: ["Interpretable", "Rápido", "Probabilidades", "No requiere normalización"],
        desventajas: ["Asume relación lineal", "Sensible a outliers", "Puede sobreajustar"]
      },
      grafico_especifico: "curva_sigmoide"
    },
    {
      id: 2,
      nombre: "K-Vecinos Más Cercanos (KNN)",
      descripcion: "Encuentra artículos similares basándose en características. Útil para casos donde artículos parecidos tienen la misma categoría.",
      tipo: "classification",
      categoria: "🤖 ALGORITMOS DE CLASIFICACIÓN",
      accuracy: 0.9810,
      auc: 0.9886,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "texto_tfidf"],
      proceso: [
        "Cálculo de distancias euclidianas entre artículos",
        "Selección de k vecinos más cercanos",
        "Votación por mayoría de clases de vecinos",
        "Optimización del parámetro k"
      ],
      explicacion: {
        que_es: "Algoritmo basado en instancias que clasifica según los vecinos más cercanos.",
        como_funciona: "Encuentra los k puntos más cercanos y asigna la clase más común entre ellos.",
        ventajas: ["Simple", "No paramétrico", "Efectivo con datos no lineales", "Encuentra patrones locales"],
        desventajas: ["Computacionalmente costoso", "Sensible a la escala", "Requiere mucha memoria"]
      },
      grafico_especifico: "distancia_vecinos"
    },
    {
      id: 3,
      nombre: "Naive Bayes",
      descripcion: "Basado en probabilidad condicionada. Ideal para clasificación de texto y noticias.",
      tipo: "classification",
      categoria: "🤖 ALGORITMOS DE CLASIFICACIÓN",
      accuracy: 0.9524,
      auc: 0.9696,
      variables: ["texto_tfidf", "longitud_titulo", "longitud_resumen", "cantidad_palabras"],
      proceso: [
        "Cálculo de probabilidades P(característica|clase)",
        "Aplicación del teorema de Bayes",
        "Selección de clase con mayor probabilidad",
        "Suavizado de Laplace para evitar ceros"
      ],
      explicacion: {
        que_es: "Clasificador probabilístico basado en el teorema de Bayes.",
        como_funciona: "Calcula probabilidades posteriores asumiendo independencia entre características.",
        ventajas: ["Muy rápido", "Funciona bien con texto", "Robusto a ruido", "Ideal para noticias"],
        desventajas: ["Asunción de independencia", "Puede ser ingenuo", "Sensible a características irrelevantes"]
      },
      grafico_especifico: "probabilidades_bayes"
    },
    {
      id: 4,
      nombre: "Árbol de Decisión",
      descripcion: "Modelo interpretable que muestra reglas de decisión. Fácil de entender cómo clasifica.",
      tipo: "classification",
      categoria: "🤖 ALGORITMOS DE CLASIFICACIÓN",
      accuracy: 1.0000,
      auc: 1.0000,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "categoria_encoded"],
      proceso: [
        "Cálculo de ganancia de información para cada característica",
        "División recursiva de nodos basada en umbrales",
        "Criterio de parada para evitar sobreajuste",
        "Podado del árbol para generalización"
      ],
      explicacion: {
        que_es: "Modelo que utiliza estructura de árbol para tomar decisiones basadas en reglas.",
        como_funciona: "Divide recursivamente los datos en subconjuntos basándose en características.",
        ventajas: ["Fácil de interpretar", "No requiere normalización", "Maneja datos faltantes", "Reglas claras"],
        desventajas: ["Tendencia al sobreajuste", "Inestable", "Sesgo hacia características con muchos valores"]
      },
      grafico_especifico: "arbol_decision"
    },
    {
      id: 5,
      nombre: "Random Forest",
      descripcion: "Evolución de árboles de decisión, más estable. Reduce overfitting usando múltiples árboles.",
      tipo: "classification",
      categoria: "🤖 ALGORITMOS DE CLASIFICACIÓN",
      accuracy: 0.9905,
      auc: 0.9994,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "categoria_encoded", "texto_tfidf"],
      proceso: [
        "Bootstrap: Creación de muestras aleatorias con reemplazo",
        "Entrenamiento paralelo de múltiples árboles",
        "Votación por mayoría de predicciones",
        "Cálculo de importancia de características"
      ],
      explicacion: {
        que_es: "Ensemble que combina múltiples árboles de decisión para mayor robustez.",
        como_funciona: "Entrena múltiples árboles en subconjuntos aleatorios y combina sus predicciones.",
        ventajas: ["Muy preciso", "Robusto al sobreajuste", "Proporciona importancia de características", "Estable"],
        desventajas: ["Menos interpretable", "Más lento", "Requiere más memoria"]
      },
      grafico_especifico: "bosque_arboles"
    },
    {
      id: 6,
      nombre: "XGBoost",
      descripcion: "Algoritmo avanzado basado en boosting. Muy potente para datos tabulares.",
      tipo: "classification",
      categoria: "🤖 ALGORITMOS DE CLASIFICACIÓN",
      accuracy: 0.9950,
      auc: 0.9995,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "categoria_encoded", "texto_tfidf"],
      proceso: [
        "Inicialización con predicción base",
        "Cálculo de residuos del modelo actual",
        "Entrenamiento secuencial de modelos correctores",
        "Combinación ponderada de todas las predicciones"
      ],
      explicacion: {
        que_es: "Implementación optimizada de gradient boosting para máxima precisión.",
        como_funciona: "Entrena modelos secuencialmente, cada uno corrige errores del anterior.",
        ventajas: ["Muy preciso", "Rápido", "Maneja datos faltantes", "Eficiente en memoria"],
        desventajas: ["Complejo de interpretar", "Sensible a parámetros", "Puede sobreajustar"]
      },
      grafico_especifico: "gradient_boosting"
    },
    {
      id: 7,
      nombre: "LightGBM",
      descripcion: "Más rápido que XGBoost. Ideal para proyectos empresariales.",
      tipo: "classification",
      categoria: "🤖 ALGORITMOS DE CLASIFICACIÓN",
      accuracy: 0.9968,
      auc: 0.9999,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "categoria_encoded", "texto_tfidf"],
      proceso: [
        "Inicialización con predicción base",
        "Cálculo de residuos del modelo actual",
        "Entrenamiento secuencial de modelos correctores",
        "Combinación ponderada de todas las predicciones"
      ],
      explicacion: {
        que_es: "Implementación optimizada de gradient boosting para máxima precisión.",
        como_funciona: "Entrena modelos secuencialmente, cada uno corrige errores del anterior.",
        ventajas: ["Muy preciso", "Rápido", "Maneja datos faltantes", "Eficiente en memoria"],
        desventajas: ["Complejo de interpretar", "Sensible a parámetros", "Puede sobreajustar"]
      },
      grafico_especifico: "lightgbm_optimization"
    },
    // 🔍 ALGORITMO DE CLUSTERING (1 método)
    {
      id: 8,
      nombre: "K-Means",
      descripcion: "Clustering no supervisado. Agrupa artículos similares sin etiquetas.",
      tipo: "clustering",
      categoria: "🔍 ALGORITMO DE CLUSTERING",
      accuracy: 0.3541,
      auc: 0.3541,
      variables: ["longitud_titulo", "longitud_resumen", "cantidad_palabras", "periodico_encoded", "categoria_encoded"],
      proceso: [
        "Inicialización aleatoria de k centroides",
        "Asignación de puntos al centroide más cercano",
        "Recálculo de centroides basado en asignaciones",
        "Repetición hasta convergencia"
      ],
      explicacion: {
        que_es: "Algoritmo de clustering no supervisado que agrupa datos en k clusters.",
        como_funciona: "Inicializa k centroides, asigna puntos al más cercano y recalcula centroides.",
        ventajas: ["Simple y rápido", "Escalable", "Efectivo con clusters esféricos", "No requiere etiquetas"],
        desventajas: ["Requiere especificar k", "Sensible a inicialización", "Asume clusters esféricos"]
      },
      grafico_especifico: "clustering_kmeans"
    },
    // 📈 ALGORITMOS DE SERIES TEMPORALES (2 métodos)
    {
      id: 9,
      nombre: "ARIMA",
      descripcion: "Modelo estadístico para pronósticos temporales. Analiza patrones en fechas de publicación.",
      tipo: "temporal",
      categoria: "📈 ALGORITMOS DE SERIES TEMPORALES",
      accuracy: 0.8200,
      auc: 0.8200,
      variables: ["fecha_extraccion", "es_importante", "categoria_encoded", "periodico_encoded"],
      proceso: [
        "Análisis de estacionariedad de la serie temporal",
        "Identificación de parámetros ARIMA (p,d,q)",
        "Estimación de parámetros del modelo",
        "Validación con residuos y predicciones"
      ],
      explicacion: {
        que_es: "Análisis de series temporales para identificar patrones temporales.",
        como_funciona: "ARIMA combina componentes autoregresivos, integración y media móvil.",
        ventajas: ["Específico para tiempo", "Identifica tendencias", "Predicciones futuras", "Maneja estacionalidad"],
        desventajas: ["Requiere datos temporales", "Complejo de configurar", "Sensible a outliers"]
      },
      grafico_especifico: "serie_temporal_arima"
    },
    {
      id: 10,
      nombre: "Suavizado Exponencial",
      descripcion: "Utiliza medias móviles para predicciones. Útil para tendencias temporales de noticias.",
      tipo: "temporal",
      categoria: "📈 ALGORITMOS DE SERIES TEMPORALES",
      accuracy: 0.8500,
      auc: 0.8500,
      variables: ["fecha_extraccion", "es_importante", "categoria_encoded", "periodico_encoded"],
      proceso: [
        "Cálculo de medias móviles exponenciales",
        "Aplicación de factores de suavizado",
        "Predicción basada en tendencias históricas",
        "Ajuste de parámetros de suavizado"
      ],
      explicacion: {
        que_es: "Método de suavizado para análisis de tendencias temporales.",
        como_funciona: "Utiliza medias móviles exponenciales para predecir valores futuros.",
        ventajas: ["Simple de implementar", "Efectivo para tendencias", "Rápido", "Fácil de interpretar"],
        desventajas: ["Limitado a tendencias simples", "No maneja estacionalidad compleja", "Sensible a outliers"]
      },
      grafico_especifico: "suavizado_exponencial"
    }
  ];

  const chartData = [
    { name: 'Regresión Logística', accuracy: 97.78, auc: 97.87 },
    { name: 'K-Nearest Neighbors', accuracy: 98.10, auc: 98.86 },
    { name: 'Naive Bayes', accuracy: 95.24, auc: 96.96 },
    { name: 'Árbol de Decisión', accuracy: 100.00, auc: 100.00 },
    { name: 'Random Forest', accuracy: 99.05, auc: 99.94 },
    { name: 'XGBoost', accuracy: 99.50, auc: 99.95 },
    { name: 'LightGBM', accuracy: 99.68, auc: 99.99 },
    { name: 'K-Means', accuracy: 35.41, auc: 35.41 },
    { name: 'ARIMA', accuracy: 82.00, auc: 82.00 },
    { name: 'Suavizado Exponencial', accuracy: 85.00, auc: 85.00 }
  ];

  const processSteps = [
    { title: "Carga de Datos", description: "1,571 artículos periodísticos cargados", progress: 20 },
    { title: "Limpieza de Texto", description: "Procesamiento de títulos, resúmenes y contenido", progress: 40 },
    { title: "Vectorización TF-IDF", description: "1,000 características de texto generadas", progress: 60 },
    { title: "Entrenamiento ML", description: "10 algoritmos entrenados", progress: 80 },
    { title: "Evaluación", description: "Métricas calculadas y comparación", progress: 100 }
  ];

  const toggleMethod = (methodId) => {
    if (selectedMethods.includes(methodId)) {
      setSelectedMethods(selectedMethods.filter(id => id !== methodId));
    } else {
      setSelectedMethods([...selectedMethods, methodId]);
    }
  };

  const executeAnalysis = () => {
    if (selectedMethods.length === 0) {
      alert('Por favor selecciona al menos un método para analizar.');
      return;
    }

    setIsAnalyzing(true);
    setCurrentStep(0);

    const interval = setInterval(() => {
      setCurrentStep(prev => {
        if (prev >= processSteps.length - 1) {
          clearInterval(interval);
          setIsAnalyzing(false);
          setAnalysisResults({
            selectedMethods: selectedMethods,
            results: metodosML.filter(m => selectedMethods.includes(m.id)),
            bestMethod: metodosML.find(m => m.id === selectedMethods[0])
          });
          return prev;
        }
        return prev + 1;
      });
    }, 1000);
  };

  const getMethodTypeColor = (tipo) => {
    const colors = {
      classification: '#1890ff',
      clustering: '#722ed1',
      temporal: '#52c41a',
      ensemble: '#fa8c16'
    };
    return colors[tipo] || '#666';
  };

  const getStatusColor = (accuracy) => {
    if (accuracy >= 0.95) return '#52c41a';
    if (accuracy >= 0.90) return '#1890ff';
    if (accuracy >= 0.80) return '#faad14';
    return '#ff4d4f';
  };

  // Función para generar gráficos específicos por algoritmo
  const renderSpecificChart = (metodo) => {
    switch(metodo.grafico_especifico) {
      case 'curva_sigmoide':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <LineChart data={Array.from({length: 20}, (_, i) => ({
              x: i - 10,
              y: 1 / (1 + Math.exp(-(i - 10)))
            }))}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="x" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="y" stroke="#1890ff" strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        );

      case 'distancia_vecinos':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <ScatterChart data={Array.from({length: 50}, (_, i) => ({
              x: Math.random() * 100,
              y: Math.random() * 100,
              cluster: Math.floor(Math.random() * 3)
            }))}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="x" />
              <YAxis dataKey="y" />
              <Tooltip />
              <Scatter dataKey="cluster" fill="#52c41a" />
            </ScatterChart>
          </ResponsiveContainer>
        );

      case 'probabilidades_bayes':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <BarChart data={[
              { name: 'P(Importante|Texto)', value: 0.75 },
              { name: 'P(No Importante|Texto)', value: 0.25 },
              { name: 'P(Importante|Longitud)', value: 0.60 },
              { name: 'P(No Importante|Longitud)', value: 0.40 }
            ]}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
              <YAxis />
              <Tooltip />
              <Bar dataKey="value" fill="#722ed1" />
            </BarChart>
          </ResponsiveContainer>
        );

      case 'arbol_decision':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <BarChart data={[
              { name: 'Longitud > 50', value: 85 },
              { name: 'Periódico = La Vanguardia', value: 70 },
              { name: 'Categoría = Internacional', value: 60 },
              { name: 'Palabras > 100', value: 45 }
            ]}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
              <YAxis />
              <Tooltip />
              <Bar dataKey="value" fill="#52c41a" />
            </BarChart>
          </ResponsiveContainer>
        );

      case 'bosque_arboles':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <BarChart data={[
              { name: 'Árbol 1', value: 95 },
              { name: 'Árbol 2', value: 92 },
              { name: 'Árbol 3', value: 98 },
              { name: 'Árbol 4', value: 94 },
              { name: 'Árbol 5', value: 96 }
            ]}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="value" fill="#fa8c16" />
            </BarChart>
          </ResponsiveContainer>
        );

      case 'gradient_boosting':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <LineChart data={Array.from({length: 10}, (_, i) => ({
              iteracion: i + 1,
              error: 100 - (i * 8 + Math.random() * 5)
            }))}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="iteracion" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="error" stroke="#ff4d4f" strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        );

      case 'lightgbm_optimization':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <AreaChart data={Array.from({length: 15}, (_, i) => ({
              iteracion: i + 1,
              accuracy: 70 + (i * 2) + Math.random() * 3
            }))}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="iteracion" />
              <YAxis />
              <Tooltip />
              <Area type="monotone" dataKey="accuracy" stroke="#52c41a" fill="#52c41a" fillOpacity={0.3} />
            </AreaChart>
          </ResponsiveContainer>
        );

      case 'clustering_kmeans':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <ScatterChart data={Array.from({length: 100}, (_, i) => ({
              x: Math.random() * 100,
              y: Math.random() * 100,
              cluster: Math.floor(Math.random() * 5)
            }))}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="x" />
              <YAxis dataKey="y" />
              <Tooltip />
              <Scatter dataKey="cluster" fill="#722ed1" />
            </ScatterChart>
          </ResponsiveContainer>
        );

      case 'serie_temporal_arima':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <LineChart data={Array.from({length: 30}, (_, i) => ({
              fecha: `Día ${i + 1}`,
              valor: 50 + Math.sin(i * 0.5) * 20 + Math.random() * 10,
              prediccion: 50 + Math.sin(i * 0.5) * 20
            }))}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="fecha" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="valor" stroke="#1890ff" strokeWidth={2} />
              <Line type="monotone" dataKey="prediccion" stroke="#ff4d4f" strokeWidth={2} strokeDasharray="5 5" />
            </LineChart>
          </ResponsiveContainer>
        );

      case 'suavizado_exponencial':
        return (
          <ResponsiveContainer width="100%" height={200}>
            <LineChart data={Array.from({length: 20}, (_, i) => ({
              tiempo: i + 1,
              original: 50 + Math.random() * 20,
              suavizado: 50 + Math.sin(i * 0.3) * 10
            }))}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="tiempo" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="original" stroke="#faad14" strokeWidth={2} />
              <Line type="monotone" dataKey="suavizado" stroke="#52c41a" strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        );

      default:
        return <div>Gráfico no disponible</div>;
    }
  };

  // Agrupar métodos por categoría
  const metodosPorCategoria = metodosML.reduce((acc, metodo) => {
    if (!acc[metodo.categoria]) {
      acc[metodo.categoria] = [];
    }
    acc[metodo.categoria].push(metodo);
    return acc;
  }, {});

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
      }}>
        <div style={{ textAlign: 'center', color: 'white' }}>
          <h2>🔄 Cargando datos del análisis...</h2>
          <p>Preparando resultados de los 10 algoritmos de ML</p>
        </div>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', minHeight: '100vh' }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ textAlign: 'center', color: 'white', marginBottom: '30px' }}>
          <h1 style={{ fontSize: '2.5em', marginBottom: '10px', textShadow: '2px 2px 4px rgba(0,0,0,0.3)' }}>
            🤖 Dashboard ML Completo
          </h1>
          <p style={{ fontSize: '1.2em', opacity: 0.9 }}>
            Análisis de {dashboardData?.dataset_info?.total_articulos || 1571} Artículos Periodísticos con 10 Métodos de Machine Learning
          </p>
          {dashboardData?.resumen_analisis?.mejor_algoritmo && (
            <div style={{ marginTop: '15px', padding: '10px', background: 'rgba(255,255,255,0.1)', borderRadius: '10px' }}>
              <h3>🏆 Mejor Algoritmo: {dashboardData.resumen_analisis.mejor_algoritmo.nombre}</h3>
              <p>Accuracy: {(dashboardData.resumen_analisis.mejor_algoritmo.accuracy * 100).toFixed(2)}% | AUC: {dashboardData.resumen_analisis.mejor_algoritmo.auc.toFixed(4)}</p>
            </div>
          )}
        </div>

        {/* Proceso de Análisis */}
        <Card style={{ marginBottom: '30px', borderRadius: '15px' }}>
          <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>📊 Proceso de Análisis de Datos</h2>

          <Row gutter={[16, 16]}>
            {processSteps.map((step, index) => (
              <Col xs={24} sm={12} md={8} lg={4} key={index}>
                <Card 
                  size="small" 
                  style={{ 
                    textAlign: 'center',
                    background: index <= currentStep ? '#f6ffed' : '#f5f5f5',
                    border: index <= currentStep ? '2px solid #52c41a' : '1px solid #d9d9d9'
                  }}
                >
                  <div style={{ 
                    width: '30px', 
                    height: '30px', 
                    borderRadius: '50%', 
                    background: index <= currentStep ? '#52c41a' : '#d9d9d9',
                    color: 'white',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    margin: '0 auto 10px',
                    fontWeight: 'bold'
                  }}>
                    {index + 1}
                  </div>
                  <h4 style={{ margin: '0 0 5px', fontSize: '14px' }}>{step.title}</h4>
                  <p style={{ margin: 0, fontSize: '12px', color: '#666' }}>{step.description}</p>
                  <Progress percent={step.progress} size="small" />
                </Card>
              </Col>
            ))}
          </Row>
        </Card>

        {/* Métodos de ML organizados por categorías */}
        <Card style={{ marginBottom: '30px', borderRadius: '15px' }}>
          <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>🎯 Selecciona los Métodos de ML</h2>

          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            <Button 
              type="primary" 
              size="large" 
              onClick={executeAnalysis}
              loading={isAnalyzing}
              style={{ marginRight: '10px' }}
            >
              🚀 Ejecutar Análisis
            </Button>
            <Button 
              onClick={() => setSelectedMethods([])}
              style={{ marginRight: '10px' }}
            >
              🔄 Limpiar Selección
            </Button>
            <Button type="default">
              📈 Ver Proceso Completo
            </Button>
          </div>

          {Object.entries(metodosPorCategoria).map(([categoria, metodos]) => (
            <div key={categoria} style={{ marginBottom: '30px' }}>
              <Divider orientation="left">
                <Tag color="blue" style={{ fontSize: '16px', padding: '5px 10px' }}>
                  {categoria}
                </Tag>
              </Divider>

              <Row gutter={[16, 16]}>
                {metodos.map((metodo) => (
                  <Col xs={24} sm={12} lg={8} xl={6} key={metodo.id}>
                    <Card
                      hoverable
                      style={{
                        cursor: 'pointer',
                        border: selectedMethods.includes(metodo.id) ? '3px solid #52c41a' : '1px solid #d9d9d9',
                        background: selectedMethods.includes(metodo.id) ? '#f6ffed' : 'white'
                      }}
                      onClick={() => toggleMethod(metodo.id)}
                    >
                      <div style={{ position: 'relative' }}>
                        <div style={{
                          position: 'absolute',
                          top: '10px',
                          right: '10px',
                          width: '30px',
                          height: '30px',
                          borderRadius: '50%',
                          background: '#1890ff',
                          color: 'white',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          fontWeight: 'bold',
                          fontSize: '14px'
                        }}>
                          {metodo.id}
                        </div>

                        <h3 style={{ marginBottom: '10px', paddingRight: '40px' }}>{metodo.nombre}</h3>
                        <p style={{ color: '#666', marginBottom: '15px', fontSize: '14px' }}>
                          {metodo.descripcion}
                        </p>

                        <Row gutter={8} style={{ marginBottom: '15px' }}>
                          <Col span={12}>
                            <Statistic 
                              title="Accuracy" 
                              value={metodo.accuracy * 100} 
                              precision={1} 
                              suffix="%" 
                              valueStyle={{ color: getStatusColor(metodo.accuracy), fontSize: '16px' }}
                            />
                          </Col>
                          <Col span={12}>
                            <Statistic 
                              title="AUC" 
                              value={metodo.auc} 
                              precision={3}
                              valueStyle={{ color: getStatusColor(metodo.auc), fontSize: '16px' }}
                            />
                          </Col>
                        </Row>

                        <div style={{
                          display: 'inline-block',
                          padding: '4px 8px',
                          borderRadius: '12px',
                          fontSize: '12px',
                          fontWeight: 'bold',
                          textTransform: 'uppercase',
                          background: getMethodTypeColor(metodo.tipo) + '20',
                          color: getMethodTypeColor(metodo.tipo)
                        }}>
                          {metodo.tipo}
                        </div>
                      </div>
                    </Card>
                  </Col>
                ))}
              </Row>
            </div>
          ))}
        </Card>

        {/* Resultados del Análisis */}
        {analysisResults && (
          <Card style={{ borderRadius: '15px' }}>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>📊 Resultados del Análisis</h2>

            <Tabs defaultActiveKey="1">
              <TabPane tab="📈 Gráficos de Comparación" key="1">
                <Row gutter={[16, 16]}>
                  <Col xs={24} lg={12}>
                    <Card title="Comparación de Accuracy" size="small">
                      <ResponsiveContainer width="100%" height={300}>
                        <BarChart data={chartData.filter(d => analysisResults.selectedMethods.includes(metodosML.find(m => m.nombre === d.name)?.id))}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                          <YAxis />
                          <Tooltip />
                          <Bar dataKey="accuracy" fill="#1890ff" />
                        </BarChart>
                      </ResponsiveContainer>
                    </Card>
                  </Col>

                  <Col xs={24} lg={12}>
                    <Card title="Comparación de AUC" size="small">
                      <ResponsiveContainer width="100%" height={300}>
                        <BarChart data={chartData.filter(d => analysisResults.selectedMethods.includes(metodosML.find(m => m.nombre === d.name)?.id))}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="name" angle={-45} textAnchor="end" height={100} />
                          <YAxis />
                          <Tooltip />
                          <Bar dataKey="auc" fill="#52c41a" />
                        </BarChart>
                      </ResponsiveContainer>
                    </Card>
                  </Col>
                </Row>
              </TabPane>

              <TabPane tab="🔍 Detalles por Método" key="2">
                {analysisResults.results.map((metodo, index) => (
                  <Card key={metodo.id} style={{ marginBottom: '20px' }}>
                    <h3>{index + 1}. {metodo.nombre}</h3>

                    <Row gutter={[16, 16]}>
                      <Col xs={24} md={12}>
                        <Alert
                          message="¿Qué es?"
                          description={metodo.explicacion.que_es}
                          type="info"
                          showIcon
                          style={{ marginBottom: '10px' }}
                        />
                        <Alert
                          message="¿Cómo funciona?"
                          description={metodo.explicacion.como_funciona}
                          type="info"
                          showIcon
                        />
                      </Col>

                      <Col xs={24} md={12}>
                        <h4>Variables Utilizadas:</h4>
                        <div style={{ marginBottom: '15px' }}>
                          {metodo.variables.map((variable, i) => (
                            <span key={i} style={{
                              display: 'inline-block',
                              background: '#f0f0f0',
                              padding: '4px 8px',
                              margin: '2px',
                              borderRadius: '4px',
                              fontSize: '12px'
                            }}>
                              {variable}
                            </span>
                          ))}
                        </div>

                        <h4>Proceso Paso a Paso:</h4>
                        <ol style={{ paddingLeft: '20px' }}>
                          {metodo.proceso.map((paso, i) => (
                            <li key={i} style={{ marginBottom: '5px', fontSize: '14px' }}>{paso}</li>
                          ))}
                        </ol>
                      </Col>
                    </Row>

                    {/* Gráfico específico del algoritmo */}
                    <Row gutter={[16, 16]} style={{ marginTop: '20px' }}>
                      <Col span={24}>
                        <Card title={`Gráfico Específico: ${metodo.nombre}`} size="small">
                          {renderSpecificChart(metodo)}
                        </Card>
                      </Col>
                    </Row>

                    <Row gutter={16}>
                      <Col span={12}>
                        <h4>Ventajas:</h4>
                        <ul>
                          {metodo.explicacion.ventajas.map((ventaja, i) => (
                            <li key={i} style={{ color: '#52c41a' }}>
                              <CheckCircleOutlined style={{ marginRight: '5px' }} />
                              {ventaja}
                            </li>
                          ))}
                        </ul>
                      </Col>
                      <Col span={12}>
                        <h4>Desventajas:</h4>
                        <ul>
                          {metodo.explicacion.desventajas.map((desventaja, i) => (
                            <li key={i} style={{ color: '#ff4d4f' }}>
                              <CloseCircleOutlined style={{ marginRight: '5px' }} />
                              {desventaja}
                            </li>
                          ))}
                        </ul>
                      </Col>
                    </Row>
                  </Card>
                ))}
              </TabPane>

              <TabPane tab="📊 Gráficos Detallados" key="3">
                <Row gutter={[16, 16]}>
                  {analysisResults.results.map((metodo, index) => (
                    <Col xs={24} lg={12} key={metodo.id}>
                      <Card title={`Gráficos de ${metodo.nombre}`} style={{ marginBottom: '20px' }}>
                        <Row gutter={[16, 16]}>
                          <Col span={24}>
                            <h4>Rendimiento del Método</h4>
                            <ResponsiveContainer width="100%" height={200}>
                              <BarChart data={[
                                { name: 'Accuracy', value: metodo.accuracy * 100 },
                                { name: 'AUC', value: metodo.auc * 100 }
                              ]}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="name" />
                                <YAxis />
                                <Tooltip formatter={(value) => `${value.toFixed(1)}%`} />
                                <Bar dataKey="value" fill="#1890ff" />
                              </BarChart>
                            </ResponsiveContainer>
                          </Col>

                          <Col span={24}>
                            <h4>Gráfico Específico del Algoritmo</h4>
                            {renderSpecificChart(metodo)}
                          </Col>
                        </Row>

                        <div style={{ marginTop: '15px', padding: '10px', background: '#f5f5f5', borderRadius: '5px' }}>
                          <h5>Variables Utilizadas:</h5>
                          <div>
                            {metodo.variables.map((variable, i) => (
                              <span key={i} style={{
                                display: 'inline-block',
                                background: '#1890ff',
                                color: 'white',
                                padding: '2px 6px',
                                margin: '2px',
                                borderRadius: '3px',
                                fontSize: '11px'
                              }}>
                                {variable}
                              </span>
                            ))}
                          </div>
                        </div>
                      </Card>
                    </Col>
                  ))}
                </Row>
              </TabPane>

              <TabPane tab="🏆 Comparación Final" key="4">
                {dashboardData?.resumen_analisis?.mejor_algoritmo && (
                  <Alert
                    message="Mejor Método Identificado"
                    description={`${dashboardData.resumen_analisis.mejor_algoritmo.nombre} con Accuracy: ${(dashboardData.resumen_analisis.mejor_algoritmo.accuracy * 100).toFixed(1)}% y AUC: ${dashboardData.resumen_analisis.mejor_algoritmo.auc.toFixed(3)}`}
                    type="success"
                    showIcon
                    style={{ marginBottom: '20px' }}
                  />
                )}

                <Table
                  dataSource={metodosML.map((metodo) => ({
                    key: metodo.id,
                    ranking: metodo.ranking || metodo.id,
                    metodo: metodo.nombre,
                    accuracy: `${(metodo.accuracy * 100).toFixed(1)}%`,
                    auc: metodo.auc.toFixed(3),
                    tipo: metodo.tipo,
                    categoria: metodo.categoria,
                    estado: metodo.estado || (metodo.accuracy >= 0.95 ? 'Excelente' : 
                           metodo.accuracy >= 0.90 ? 'Bueno' : 
                           metodo.accuracy >= 0.80 ? 'Regular' : 'Pobre')
                  }))}
                  columns={[
                    { title: 'Ranking', dataIndex: 'ranking', key: 'ranking' },
                    { title: 'Método', dataIndex: 'metodo', key: 'metodo' },
                    { title: 'Accuracy', dataIndex: 'accuracy', key: 'accuracy' },
                    { title: 'AUC', dataIndex: 'auc', key: 'auc' },
                    { title: 'Categoría', dataIndex: 'categoria', key: 'categoria' },
                    { title: 'Estado', dataIndex: 'estado', key: 'estado' }
                  ]}
                  pagination={false}
                />

                {dashboardData?.resumen_analisis?.recomendaciones && (
                  <Card title="💡 Recomendaciones" style={{ marginTop: '20px' }}>
                    <ul>
                      {dashboardData.resumen_analisis.recomendaciones.map((rec, index) => (
                        <li key={index} style={{ marginBottom: '10px' }}>{rec}</li>
                      ))}
                    </ul>
                  </Card>
                )}
              </TabPane>
            </Tabs>
          </Card>
        )}
      </div>
    </div>
  );
}

export default App;
